mapper.select:
SELECT ROWNUM,y.sum,y.ORG_NAME from
(SELECT count(ORG_NAME) sum,ORG_NAME from ACCEPT_BUSINESS_INDEX
WHERE REGION_CODE = '370100000000'

    and to_char(SUBMIT_TIME,'yyyy') = #{year}


    and to_char(SUBMIT_TIME,'yyyy-mm') = #{month}


    and to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

GROUP BY ORG_NAME ORDER BY sum desc) y

    WHERE ROWNUM <=  #{page}

SELECT ROWNUM,y.ORG_NAME,y.sum from
(SELECT count(ORG_NAME) sum,ORG_NAME from ACCEPT_BUSINESS_INDEX
WHERE REGION_CODE = '370100000000'

    and to_char(SUBMIT_TIME,'yyyy') = #{year}


    and to_char(SUBMIT_TIME,'yyyy-mm') = #{month}


    and to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

GROUP BY ORG_NAME ORDER BY sum desc) y

    WHERE ROWNUM <=  #{page}

SELECT ROWNUM,y.NUM,y.HANDLE_USER_NAME from (SELECT COUNT (*) NUM, HANDLE_USER_NAME FROM ACCEPT_BUSINESS_INDEX i INNER JOIN ACCEPT_AREA_TREE A ON i.ORG_CODE = A .CODE WHERE HANDLE_USER_NAME is not null and i.REGION_CODE = '370100000000'
and to_char(RECEIVE_TIME,'yyyy') = #{year}
and to_char(RECEIVE_TIME,'yyyy-mm') = #{month}
and to_char(RECEIVE_TIME,'yyyy-mm-dd') = #{day}
GROUP BY HANDLE_USER_NAME order by NUM desc) y
WHERE ROWNUM <= #{page}

SELECT ROWNUM,y.CODE,y.SUM from (SELECT WIN.WINDOW_SHOW_CODE AS CODE, COUNT( WIN.WINDOW_SHOW_CODE ) AS SUM FROM ACCEPT_WINDOWS WIN LEFT JOIN ACCEPT_BUSINESS_INDEX IND ON IND.WINDOW_ID = WIN.WINDOW_ID WHERE
to_char(IND.SUBMIT_TIME,'yyyy') = #{year}
to_char(IND.SUBMIT_TIME,'yyyy-mm') = #{month}
to_char(IND.SUBMIT_TIME,'yyyy-mm-dd') = #{day}
GROUP BY WIN.WINDOW_SHOW_CODE order by SUM desc) y
where ROWNUM <= #{page}

SELECT ROWNUM,y.CODE,y.SUM from (SELECT WIN.WINDOW_SHOW_CODE AS CODE, COUNT( WIN.WINDOW_SHOW_CODE ) AS SUM FROM ACCEPT_WINDOWS WIN LEFT JOIN ACCEPT_BUSINESS_INDEX IND ON IND.WINDOW_ID = WIN.WINDOW_ID WHERE
to_char(IND.SUBMIT_TIME,'yyyy') = #{year}
to_char(IND.SUBMIT_TIME,'yyyy-mm') = #{month}
to_char(IND.SUBMIT_TIME,'yyyy-mm-dd') = #{day}
GROUP BY WIN.WINDOW_SHOW_CODE order by SUM desc) y

select   ROWNUM, ITEM_NAME,TO_CHAR(SUBMIT_TIME,'yyyy-mm-dd hh24:mi:ss') SUBMIT_TIME,
case
when STATE = '99' then '准予许可'
end STATE ,WINDOW_SHOW_CODE
from ACCEPT_BUSINESS_INDEX
where STATE='99' and region_code='370100000000'
and

    to_char(SUBMIT_TIME,'yyyy') = #{year}


    to_char(SUBMIT_TIME,'yyyy-mm') = #{month}


    to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

select ROWNUM, ITEM_NAME,TO_CHAR(SUBMIT_TIME,'yyyy-mm-dd hh24:mi:ss') SUBMIT_TIME, case
when STATE = '00' THEN '草稿' when STATE = '01' then '办理中' when STATE = '02' then '不予受理' when STATE = '03' then '预审撤销' when STATE = '11' then '待预审' when STATE = '12' then '预审通过' when STATE = '13' then '删除预审' when STATE = '14' THEN '外网预审驳回' when STATE = '15' then '受理通过' when STATE = '16' then '预审业务撤销' when STATE = '21' then '补齐补正' when STATE = '23' then '挂起' when STATE = '24' THEN '收费挂起' when STATE = '25' THEN '挂起审核状态' when STATE = '26' THEN '回退' when STATE = '90' THEN '' when STATE = '94' THEN '待办结签收' when STATE = '95' THEN '待办结' when STATE = '96' then '退件' when STATE = '97' then '作废' when STATE = '98' then '不予许可' when STATE = '99' then '准予许可' when STATE = '100' THEN '已出件'
end STATE ,WINDOW_SHOW_CODE from ACCEPT_BUSINESS_INDEX where region_code='370100000000' and
to_char(SUBMIT_TIME,'yyyy') = #{year}
to_char(SUBMIT_TIME,'yyyy-mm') = #{month}
to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

select   ROWNUM, ITEM_NAME,TO_CHAR(SUBMIT_TIME,'yyyy-mm-dd hh24:mi:ss') SUBMIT_TIME,
case

when STATE = '99' then '准予许可'

end STATE ,WINDOW_SHOW_CODE
from ACCEPT_BUSINESS_INDEX
where STATE='99' and region_code='370100000000'
and

    to_char(SUBMIT_TIME,'yyyy') = #{year}


    to_char(SUBMIT_TIME,'yyyy-mm') = #{month}


    to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

select ROWNUM, ITEM_NAME,TO_CHAR(SUBMIT_TIME,'yyyy-mm-dd hh24:mi:ss') SUBMIT_TIME, case
when STATE = '00' THEN '草稿' when STATE = '01' then '办理中' when STATE = '02' then '不予受理' when STATE = '03' then '预审撤销' when STATE = '11' then '待预审' when STATE = '12' then '预审通过' when STATE = '13' then '删除预审' when STATE = '14' THEN '外网预审驳回' when STATE = '15' then '受理通过' when STATE = '16' then '预审业务撤销' when STATE = '21' then '补齐补正' when STATE = '23' then '挂起' when STATE = '24' THEN '收费挂起' when STATE = '25' THEN '挂起审核状态' when STATE = '26' THEN '回退' when STATE = '90' THEN '' when STATE = '94' THEN '待办结签收' when STATE = '95' THEN '待办结' when STATE = '96' then '退件' when STATE = '97' then '作废' when STATE = '98' then '不予许可' when STATE = '99' then '准予许可' when STATE = '100' THEN '已出件'
end STATE ,WINDOW_SHOW_CODE from ACCEPT_BUSINESS_INDEX where region_code='370100000000' and
to_char(SUBMIT_TIME,'yyyy') = #{year}
to_char(SUBMIT_TIME,'yyyy-mm') = #{month}
to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}

SELECT REGION_NAME, WINDOW_SHOW_CODE, to_char ( sysdate, 'mi' ) - to_char ( SUBMIT_TIME, 'mi' ) + ( to_char ( sysdate, 'hh24' ) - to_char ( SUBMIT_TIME, 'hh24' ) ) * 60 AS SS FROM ACCEPT_BUSINESS_INDEX WHERE STATE = '15' and WINDOW_SHOW_CODE != ' ' AND
to_char(SUBMIT_TIME,'yyyy') = #{year}
to_char(SUBMIT_TIME,'yyyy-mm') = #{month}
to_char(SUBMIT_TIME,'yyyy-mm-dd') = #{day}
order by ss asc
